import React from "react";
import {
  VictoryBar,
  VictoryChart,
  VictoryAxis,
  VictoryTooltip,
  VictoryLine,
  VictoryGroup,
} from "victory";
import StudentData from "./StudentData";
// import StudentData from "./StudentData";

const wincTheme = {
  area: {
    style: {
      data: {
        fill: "#212121",
      },
      labels: {
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 12,
        letterSpacing: "normal",
        padding: 8,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
    },
    width: 350,
    height: 350,
    padding: 50,
  },
  axis: {
    style: {
      axis: {
        fill: "transparent",
        stroke: "#90A4AE",
        strokeWidth: 2,
        strokeLinecap: "round",
        strokeLinejoin: "round",
      },
      axisLabel: {
        textAnchor: "middle",
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 12,
        letterSpacing: "normal",
        padding: 8,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
      grid: {
        fill: "none",
        stroke: "none",
      },
      ticks: {
        fill: "transparent",
        size: 5,
        stroke: "#90A4AE",
        strokeWidth: 1,
        strokeLinecap: "round",
        strokeLinejoin: "round",
      },
      tickLabels: {
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 12,
        letterSpacing: "normal",
        padding: 8,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
    },
    width: 350,
    height: 350,
    padding: 50,
  },
  bar: {
    style: {
      data: {
        fill: "#4a90e2",
        padding: 0,
        strokeWidth: 0,
      },
      labels: {
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 8,
        letterSpacing: "normal",
        padding: 8,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
    },
    width: 350,
    height: 350,
    padding: 20,
  },

  chart: {
    width: 800,
    height: 300,
    padding: 50,
  },
  errorbar: {
    borderWidth: 8,
    style: {
      data: {
        fill: "transparent",
        opacity: 1,
        stroke: "#455A64",
        strokeWidth: 2,
      },
      labels: {
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 12,
        letterSpacing: "normal",
        padding: 8,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
    },
    width: 350,
    height: 350,
    padding: 50,
  },
  group: {
    colorScale: [
      "#F4511E",
      "#FFF59D",
      "#DCE775",
      "#8BC34A",
      "#00796B",
      "#006064",
    ],
    width: 350,
    height: 350,
    padding: 50,
  },
  legend: {
    colorScale: [
      "#F4511E",
      "#FFF59D",
      "#DCE775",
      "#8BC34A",
      "#00796B",
      "#006064",
    ],
    gutter: 10,
    orientation: "vertical",
    titleOrientation: "top",
    style: {
      data: {
        type: "circle",
      },
      labels: {
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 12,
        letterSpacing: "normal",
        padding: 8,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
      title: {
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 12,
        letterSpacing: "normal",
        padding: 5,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
    },
  },
  line: {
    style: {
      data: {
        fill: "transparent",
        opacity: 1,
        stroke: "#455A64",
        strokeWidth: 2,
      },
      labels: {
        fontFamily: "'Roboto', 'Helvetica Neue', Helvetica, sans-serif",
        fontSize: 12,
        letterSpacing: "normal",
        padding: 8,
        fill: "#455A64",
        stroke: "transparent",
        strokeWidth: 0,
      },
    },
    width: 350,
    height: 350,
    padding: 50,
  },
};

const ChartAvg = (props) => {
  const getRandomRating = () => 5;
  const getRandomRating2 = () => 4;

  const getAssignments = () => {
    const studentData = StudentData;
    let assignment = [];
    const map = new Map();
    for (const item of studentData) {
      if (!map.has(item.assignment)) {
        map.set(item.assignment, true);
        assignment.push({ assignment: item.assignment });
      }
    }
    // console.log(assignment);
    return assignment;
  };

  const GetAverage = () => {
    let studentData = StudentData;
    let assignment = getAssignments();
    let assingmentData = assignment.map((a) => {
      let data = {};
      return (data = studentData.filter((s) => {
        return a.assignment === s.assignment;
      }));
    });

    const count = 56;

    let difficultRating = studentData
      .map((item) => {
        return parseInt(item.difficultyRating);
      })
      .reduce((a, b) => a + b, 0);

    console.log("Difficult voor som:", difficultRating);

    difficultRating = difficultRating / count;

    let enjoymentRating = studentData
      .map((item) => {
        return parseInt(item.enjoymentRating);
      })
      .reduce((a, b) => a + b, 0);
    enjoymentRating = enjoymentRating / count;

    console.log("Difficult:", difficultRating);
    console.log("enjoy:", enjoymentRating);
    console.log("count:", count);
    console.log("assignment:", assignment);

    return {
      assignment: assignment,
      difficultRating: difficultRating,
      enjoymentRating: enjoymentRating,
    };
    return assingmentData;
  };
  GetAverage();
  console.log(GetAverage());

  let assignmentRatingAverage = [
    { assignment: "W1D1-2" },
    { assignment: "W1D1-1" },
    { assignment: "W1D1-3" },
    { assignment: "W1D2-1" },
    { assignment: "W1D2-2" },
    { assignment: "W1D3-1" },
    { assignment: "W1D3-1" },
    { assignment: "W1D3-2" },
    { assignment: "W1D3-3" },
    { assignment: "W1D3-4" },
    { assignment: "W1D3-5" },
    { assignment: "W1D3-7" },
    { assignment: "jelmer" },
  ];

  assignmentRatingAverage = assignmentRatingAverage.map((avg) => ({
    assignment: avg.assignment,
    difficultyRating: getRandomRating(),
    enjoymentRating: getRandomRating2(),
  }));

  // console.log(assignmentRatingAverage);

  // Add label
  const assignmentRatingAverageWithLabels = assignmentRatingAverage.map(
    (avg) => ({
      assignment: avg.assignment,
      difficultyRating: avg.difficultyRating,
      enjoymentRating: avg.enjoymentRating,
      label: `Opdracht ${avg.assignment},
      difficultyRating: ${avg.difficultyRating},
      enjoymentRating: ${avg.enjoymentRating}`,
    })
  );

  return (
    <React.Fragment>
      <figure>
        <div>
          {/* Eerste staaf diagram rood */}
          <VictoryChart domainPadding={15} theme={wincTheme}>
            <VictoryGroup offset={20}>
              <VictoryBar
                labelComponent={<VictoryTooltip />}
                data={assignmentRatingAverageWithLabels}
                x="assignment"
                y="difficultyRating"
                tickValues={[1, 2, 3, 4, 5]}
                tickFormat={assignmentRatingAverageWithLabels.map(
                  (avg) => avg.assignment
                )}
              />

              {/* Eerste staaf diagram geel */}
              <VictoryBar
                labelComponent={<VictoryTooltip />}
                data={assignmentRatingAverageWithLabels}
                x="assignment"
                y="enjoymentRating"
                tickValues={[1, 2, 3, 4, 5]}
                tickFormat={assignmentRatingAverageWithLabels.map(
                  (avg) => avg.assignment
                )}
              />
            </VictoryGroup>
            <VictoryAxis
              // tickValues specifies both the number of ticks and where
              // they are placed on the axis
              tickValues={[1, 2, 3, 4, 5]}
              tickFormat={assignmentRatingAverageWithLabels.map(
                (avg) => avg.assignment
              )}
            />
            <VictoryAxis dependentAxis />
          </VictoryChart>

          {/* Begin lijn diagram */}
          <VictoryChart domainPadding={15} theme={wincTheme}>
            <VictoryLine
              style={{
                data: { stroke: "#c43a31" },
                parent: { border: "1px solid #ccc" },
              }}
              data={assignmentRatingAverage}
              x="assignment"
              y="difficultyRating"
            />
            <VictoryLine
              style={{
                data: { stroke: "#ff00ff" },
                parent: { border: "1px solid #ccc" },
              }}
              data={assignmentRatingAverage}
              x="assignment"
              y="enjoymentRating"
            />
            <VictoryAxis
              // tickValues specifies both the number of ticks and where
              // they are placed on the axis
              tickValues={[1, 2, 3, 4, 5]}
              tickFormat={assignmentRatingAverage.map((avg) => avg.assignment)}
            />
            <VictoryAxis dependentAxis />
          </VictoryChart>
        </div>
      </figure>
    </React.Fragment>
  );
};

export default ChartAvg;
