import React from "react";
import StudentData from "./StudentData";
import Searchbox from "./SearchBox";

class TableView extends React.Component {
  constructor() {
    super();
    this.state = {
      filterStudent: "",
    };
  }

  handleInput = (e) => {
    // console.log(e.target.value);
    this.setState({
      searchSong: e.target.value,
    });
  };

  render() {
    let filteredStudents = this.state.StudentData.filter((item) => {
      return (
        <tr>
          <td>
            {item.name
              .toLowerCase()
              .includes(this.state.filterStudent.toLocaleLowerCase())}
          </td>
          <td>{item.assignment}</td>
          <td>{item.difficultyRating}</td>
          <td>{item.enjoymentRating}</td>
        </tr>
      );

      student.title
        .toLowerCase()
        .includes(this.state.filterStudent.toLocaleLowerCase());
    });

    let studentDataMap = StudentData.map((item) => {
      return (
        <tr>
          <td>{item.name}</td>
          <td>{item.assignment}</td>
          <td>{item.difficultyRating}</td>
          <td>{item.enjoymentRating}</td>
        </tr>
      );
    });

    return (
      <div className="tableView">
        <h1>Studenten scores</h1>
        <br />
        <div className="filter-table">
          <span>filter studenten</span>
          <select>{selectedStudents}</select>
        </div>
        <table>
          <thead>
            <th>Naam</th>
            <th>Opdracht</th>
            <th>Moeilijkheid</th>
            <th>Leukheid</th>
          </thead>
          <tbody>{filteredStudents}</tbody>
        </table>
      </div>
    );
  }
}

export default TableView;
