import React from "react";

// import Nav from "./components/Nav";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import TableView from "./components/TableView";
import StudentData from "./components/StudentData";
import ChartAvg from "./components/ChartAvg";
import Details from './components/Details'

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      studentData: StudentData,
    };
  }

  getAssignments = () => {
    const studentData = StudentData;
    let assignment = [];
    const map = new Map();
    for (const item of studentData) {
      if (!map.has(item.assignment)) {
        map.set(item.assignment, true);
        assignment.push({ assignment: item.assignment });
      }
    }
    console.log(assignment);
    return assignment;
  };

  GetAverage = () => {
    let studentData = this.state.studentData;
    let assignment = this.getAssignments();

    const count = 56;

    let difficultRating = studentData
      .map((item) => {
        return parseInt(item.difficultyRating);
      })
      .reduce((a, b) => a + b, 0);
    difficultRating = difficultRating / count;

    let enjoymentRating = studentData
      .map((item) => {
        return parseInt(item.enjoymentRating);
      })
      .reduce((a, b) => a + b, 0);
    enjoymentRating = enjoymentRating / count;

    console.log(difficultRating);
    return {
      assignment: assignment,
      difficultRating: difficultRating,
      enjoymentRating: enjoymentRating,
    };
  };

getAssignments = () => {

    let assignment = [];
    const map = new Map();
    for (const item of StudentData) {
      if (!map.has(item.assignment)) {
        map.set(item.assignment, true);
        assignment.push({ assignment: item.assignment });
      }
    }
    // console.log(assignment);
    return assignment;
  };

  // verkrijg hier de namen van de assigment en koppel met difficult en enjoy average
getNames = () => {
    let assignments = this.getAssignments();
    let assignmentsWithData = assignments.map((ass) => {
      let data = {};

      data = StudentData.filter((i) => {
        return ass.assignment === i.assignment;
      });

      const getDataLength = data.length;

      let difficultyRating = data
        .map((d) => {
          return parseInt(d.difficultyRating);
        })
        .reduce((a, b) => a + b, 0);
      difficultyRating = Math.round(difficultyRating / getDataLength);
      let enjoymentRating = data
        .map((e) => {
          return parseInt(e.enjoymentRating);
        })
        .reduce((a, b) => a + b, 0);
      enjoymentRating = Math.round(enjoymentRating / getDataLength);
      return {
        assignment: ass.assignment,
        difficultyRating: difficultyRating,
        enjoymentRating: enjoymentRating,
      };
    });
    return assignmentsWithData;
  };

  // getAssignmentsAverage();
  // console.log(getAssignmentsAverage());





  render() {
    return (
      <Router>
        <div>
          <nav>
            <ul className="nav-ul">
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/tableview">Table view</Link>
              </li>

            </ul>
          </nav>
          <main>
            <Switch>
              <Route path="/tableview">
                <TableView />
              </Route>
              <Route path="/details">
              <button>
                <Link to="/">Overview</Link>
              </button>
                <Details
                  getAssignments={this.getAssignments}
                  getAverage={this.getAverage}
                  getNames={this.getNames}
                />
              </Route>
              <Route path="/">
              <button>
                <Link to="/details">Detailed view</Link>
              </button>
                <ChartAvg
                  getAssignments={this.getAssignments}
                  getAverage={this.getAverage}
                  getNames={this.getNames}
                />
              </Route>

            </Switch>
          </main>
        </div>
      </Router>
    );
  }
}

export default App;
