import React from "react";
import StudentData from "./StudentData";

class TableView extends React.Component {
  constructor() {
    super();

    this.state = {
      tableView: {
        filterByStudent: "",
      },
    };
  }

  handleTableviewSelect = (event) => {
    event.preventDefault();

    const student = event.target.value;
    this.setState((state) => {
      state.tableView.filterByStudent = student;
      return state;
    });
  };

  render() {
    const selectedStudents = StudentData.map((item) => {
      return <option value={item.name}>{item.name}</option>;
    });

    let studentDataMap = StudentData.map((item) => {
      return (
        <tr>
          <td>{item.name}</td>
          <td>{item.assignment}</td>
          <td>{item.difficultyRating}</td>
          <td>{item.enjoymentRating}</td>
        </tr>
      );
    });

    return (
      <div className="tableView">
        <h1>Studenten scores</h1>
        <br />
        <div className="filter-table">
          <span>filter studenten</span>
          <select onChange={this.handleTableviewSelect}>
            {selectedStudents}
          </select>
        </div>
        <table>
          <thead>
            <th>Naam</th>
            <th>Opdracht</th>
            <th>Moeilijkheid</th>
            <th>Leukheid</th>
          </thead>
          <tbody>{studentDataMap}</tbody>
        </table>
      </div>
    );
  }
}

export default TableView;
